mapper.update:
        UPDATE `t_group`
        SET group_name = #{groupName}, plan_id = #{planId}, change_date = NOW()
        WHERE group_id = #{groupId};

        UPDATE `t_plan`
        SET budget = #{budget}, change_date = NOW(), plan_name = #{planName}
        WHERE plan_id = #{planId};

UPDATE `t_group_channel`
SET group_id = #{groupId}, channel_id = #{channelId}, price = #{price}
WHERE id = #{id};

mapper.delete:
DELETE FROM `t_group` g
WHERE g.`group_id` = #{groupId};
DELETE FROM `t_group_channel` c
WHERE c.`group_id` = #{groupId};
DELETE FROM `t_plan` p
WHERE p.`plan_id` = #{planId};
